-- Crear tabla si no existe
CREATE TABLE IF NOT EXISTS `documentos_estudio` (
  `id` INT(11) NOT NULL AUTO_INCREMENT,
  `area_id` INT(11) NOT NULL,
  `especialidad_id` INT(11) NOT NULL,
  `nombre_documento` VARCHAR(300) COLLATE utf8mb4_unicode_ci NOT NULL,
  `nombre_archivo` VARCHAR(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `ruta_relativa` VARCHAR(500) COLLATE utf8mb4_unicode_ci NOT NULL,
  `ruta_web` VARCHAR(500) COLLATE utf8mb4_unicode_ci,
  `tamano_kb` INT(11) DEFAULT 0,
  `extension` VARCHAR(10) DEFAULT 'pdf',
  `orden` INT(11) DEFAULT 0,
  `activo` TINYINT(1) DEFAULT 1,
  `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `idx_area` (`area_id`),
  KEY `idx_especialidad` (`especialidad_id`),
  CONSTRAINT `fk_docs_area` FOREIGN KEY (`area_id`) REFERENCES `areas` (`id`) ON DELETE CASCADE,
  CONSTRAINT `fk_docs_especialidad` FOREIGN KEY (`especialidad_id`) REFERENCES `especialidades` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


