CREATE OR REPLACE VIEW `vista_documentos_completos` AS
SELECT 
    d.id as documento_id,
    d.nombre_documento,
    d.nombre_archivo,
    d.ruta_relativa,
    d.ruta_web,
    d.tamano_kb,
    d.orden,
    a.id as area_id,
    a.nombre as area_nombre,
    e.id as especialidad_id,
    e.codigo_especialidad,
    e.nombre as especialidad_nombre,
    d.activo,
    d.created_at as fecha_subida
FROM documentos_estudio d
INNER JOIN areas a ON d.area_id = a.id
INNER JOIN especialidades e ON d.especialidad_id = e.id
WHERE d.activo = 1
ORDER BY a.id, e.codigo_especialidad, d.orden;

